(142, 48, '_form', '<div class=\"04-contact-form__form-group\">\n	<label for=\"convalidar\">¿Quiere convalidar materias?</label>\n	[radio convalidar id:convalidar use_label_element default:1 \"Si\" \"No\"]\n</div>\n<div class=\"04-contact-form__form-group\">\n	[text* nombre id:nombre \"Nombre\"]\n</div>\n<div class=\"04-contact-form__form-group\">\n	[text* apellido id:apellido \"Apellido\"]\n</div>\n<div class=\"04-contact-form__form-group\">\n	[text* pais id:pais \"País\"]\n</div>\n<div class=\"04-contact-form__form-group\">\n	[tel* telefono id:telefono \"Teléfono\"]\n</div>\n<div class=\"04-contact-form__form-group\">\n	[email* email id:email \"Email\"]\n</div>\n<div class=\"04-contact-form__form-group\">\n	[textarea* consulta id:consulta placeholder \"Consulta\"]\n</div>\n<div class=\"04-contact-form__form-group 04-contact-form__form-group--submit\">\n	[submit \"Enviar\"]\n</div>'),
